mapper.update:
UPDATE problem

    name = #{name}
    level = #{level}
    content = #{content}
    example = #{example}
    expectation = #{expectation}
    extra = #{extra}
    timeLimit = #{timeLimit}
    memoryLimit = #{memoryLimit}

WHERE id = #{id};

mapper.select:
SELECT id, name, serial, level FROM problem
WHERE serial LIKE CONCAT('%', #{keyword}, '%')
OR name LIKE CONCAT('%', #{keyword}, '%');
